interface ListItemProps extends React.ComponentPropsWithoutRef<"a"> {
  title: string;
  imageSrc?: string;
  hoverImageSrc?: string;
  altText?: string;
  svgIcon?: string;
}
const ListItem = React.forwardRef<React.ElementRef<"a">, ListItemProps>(
  (
    {
      className,
      title,
      children,
      imageSrc,
      hoverImageSrc,
      altText,
      svgIcon,
      ...props
    },
    ref
  ) => {
    return (
      <li>
        <NavigationMenuLink asChild>
          <a
            ref={ref}
            className={cn(
              "listItem group flex items-center space-x-3 rounded-md p-3 leading-none no-underline outline-none transition-all duration-300 hover:bg-[#F8F6F3] hover:text-accent-foreground focus:bg-accent focus:text-accent-foreground",
              className
            )}
            {...props}
          >
            {imageSrc && (
              <div className="relative h-12 w-12 flex-shrink-0 overflow-hidden">
                {/* Bottom image (remains visible but dimmed) */}
                <img
                  src={imageSrc}
                  alt={altText || "Image"}
                  className="absolute inset-0 h-full w-full rounded-md object-cover transition-opacity duration-300 group-hover:opacity-50"
                />
                {/* Hover image (overlays the bottom image) */}
                {hoverImageSrc && (
                  <img
                    src={hoverImageSrc}
                    alt={altText || "Hover Image"}
                    className="absolute inset-0 h-full w-full rounded-md object-cover opacity-0 transition-opacity duration-700 group-hover:opacity-100"
                  />
                )}
              </div>
            )}
            <div>
              {svgIcon && (
                <div
                  dangerouslySetInnerHTML={{ __html: svgIcon }}
                  className="mb-2"
                />
              )}
              <div className="text-sm font-medium leading-none mb-1">
                {title}
              </div>
              <p className="line-clamp-2 text-sm leading-snug text-muted-foreground">
                {children}
              </p>
            </div>
          </a>
        </NavigationMenuLink>
      </li>
    );
  }
);
ListItem.displayName = "ListItem";
